import React, { useState } from 'react';
import AuthForm from './components/AuthForm';
import PatientForm from './components/PatientForm';

function App() {
  const [user, setUser] = useState(null);
  const [record, setRecord] = useState(null);
  const [prediction, setPrediction] = useState(null);

  if (!user)
    return (
      <div style={{ textAlign: 'center', marginTop: '30px' }}>
        <h2>Login / Signup</h2>
        <div style={{ display: 'flex', justifyContent: 'center', gap: '50px' }}>
          <AuthForm mode="signup" onSuccess={setUser} />
          <AuthForm mode="login" onSuccess={setUser} />
        </div>
      </div>
    );

  return (
    <div className="container" style={{ padding: '20px' }}>
      <h2>
        Welcome {user.name} ({user.role})
      </h2>

      {/* Patient Form (only visible for patients) */}
      {user.role === 'patient' && (
        <PatientForm
          onResult={(result) => {
            setRecord(result);
            setPrediction(result); // same as record for prediction
          }}
        />
      )}

      {/* Record Preview */}
      {record && (
        <div
          style={{
            marginTop: '20px',
            padding: '15px',
            border: '1px solid #ccc',
            borderRadius: '10px',
            backgroundColor: '#f9f9f9',
          }}
        >
          <h3>Record Data</h3>
          <pre>{JSON.stringify(record, null, 2)}</pre>
        </div>
      )}

      {/* Prediction Results Section */}
      {prediction && prediction.prediction !== undefined && (
        <div
          style={{
            marginTop: '30px',
            padding: '15px',
            border: '2px solid #4caf50',
            borderRadius: '10px',
            backgroundColor: '#eafbe7',
          }}
        >
          <h3>ðŸ©º Prediction Result</h3>
          <p>
            <strong>Prediction:</strong> {prediction.prediction}
          </p>
          <p>
            <strong>Probability:</strong>{' '}
            {(prediction.probability * 100).toFixed(2)}%
          </p>
          {prediction.recommendations && (
            <>
              <h4>Recommended Actions:</h4>
              <ul>
                {prediction.recommendations.map((rec, index) => (
                  <li key={index}>
                    <strong>{rec.title}</strong>: {rec.details}
                  </li>
                ))}
              </ul>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
