import React, {useState} from 'react';
import AuthForm from './components/AuthForm';
import PatientForm from './components/PatientForm';

function App(){
  const [user,setUser] = useState(null);
  const [record,setRecord] = useState(null);
  if(!user) return <div><h2>Login / Signup</h2><AuthForm mode="signup" onSuccess={setUser} /><AuthForm mode="login" onSuccess={setUser} /></div>;
  return (
    <div className="container">
      <h2>Welcome {user.name} ({user.role})</h2>
      {user.role==='patient' && <PatientForm onResult={r=>setRecord(r)} />}
      {record && <pre>{JSON.stringify(record, null, 2)}</pre>}
    </div>
  );
}
export default App;
