import React, {useState} from 'react';
import api from '../api/api';
export default function PatientForm({onResult}) {
  const [f,setF] = useState({Pregnancies:0,Glucose:120,BloodPressure:70,SkinThickness:20,Insulin:79,BMI:25.6,DiabetesPedigreeFunction:0.5,Age:30});
  const h = e => setF({...f,[e.target.name]: Number(e.target.value)});
  const submit = async ()=> {
    const res = await api.post('/predict',{features:f,condition:'diabetes'});
    onResult(res.data.record);
  };
  return (<div>
    <input name="Glucose" onChange={h} placeholder="Glucose" />
    <input name="BMI" onChange={h} placeholder="BMI" />
    <input name="Age" onChange={h} placeholder="Age" />
    <button onClick={submit}>Predict</button>
  </div>);
}
